contracts/LayerZeroBridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

/**
 * @title LayerZeroBridge
 * @dev Cross-chain bridge contract using LayerZero for SwapScrolls
 * Enables seamless cross-chain swap execution and NFT transfers
 */
// LayerZero interfaces (simplified for demo)
interface ILayerZeroEndpoint {
    function send(
        uint16 _dstChainId,
        bytes calldata _destination,
        bytes calldata _payload,
        address payable _refundAddress,
        address _zroPaymentAddress,
        bytes calldata _adapterParams
    ) external payable;
}

contract LayerZeroBridge is Ownable, Pausable, ReentrancyGuard {
    using Strings for uint256;
    
    // Structs
    struct CrossChainSwap {
        uint256 swapId;
        address user;
        string fromToken;
        string toToken;
        uint256 amount;
        uint256 price;
        string fromChain;
        string toChain;
        uint256 timestamp;
        bool executed;
        bool confirmed;
    }
    
    struct BridgeMessage {
        uint256 messageId;
        bytes payload;
        uint16 sourceChainId;
        uint16 destinationChainId;
        uint256 timestamp;
        bool processed;
    }
    
    // State variables
    ILayerZeroEndpoint public lzEndpoint;
    mapping(uint256 => CrossChainSwap) public crossChainSwaps;
    mapping(uint256 => BridgeMessage) public bridgeMessages;
    mapping(uint16 => bool) public supportedChains;
    mapping(string => uint16) public chainNameToId;
    
    uint256 private _swapIdCounter;
    uint256 private _messageIdCounter;
    
    // Events
    event CrossChainSwapInitiated(
        uint256 indexed swapId,
        address indexed user,
        string fromToken,
        string toToken,
        string fromChain,
        string toChain
    );
    
    event CrossChainSwapExecuted(
        uint256 indexed swapId,
        string fromChain,
        string toChain,
        uint256 amount
    );
    
    event BridgeMessageReceived(
        uint256 indexed messageId,
        uint16 sourceChainId,
        uint16 destinationChainId
    );
    
    event ChainSupported(uint16 chainId, string chainName);
    event ChainRemoved(uint16 chainId);
    
    // Modifiers
    modifier onlySupportedChain(uint16 chainId) {
        require(supportedChains[chainId], "Chain not supported");
        _;
    }
    
    modifier onlyValidSwap(uint256 swapId) {
        require(crossChainSwaps[swapId].swapId != 0, "Invalid swap ID");
        _;
    }
    
    constructor(address _lzEndpoint) Ownable(msg.sender) {
        lzEndpoint = ILayerZeroEndpoint(_lzEndpoint);
        _swapIdCounter = 1;
        _messageIdCounter = 1;
        
        // Initialize supported chains
        _addSupportedChain(1, "ethereum");
        _addSupportedChain(137, "polygon");
        _addSupportedChain(42161, "arbitrum");
        _addSupportedChain(10, "optimism");
        _addSupportedChain(8453, "base");
    }
    
    /**
     * @dev Initiate a cross-chain swap
     * @param fromToken Token to swap from
     * @param toToken Token to swap to
     * @param amount Amount to swap
     * @param price Price for the swap
     * @param fromChain Source chain
     * @param toChain Destination chain
     */
    function initiateCrossChainSwap(
        string memory fromToken,
        string memory toToken,
        uint256 amount,
        uint256 price,
        string memory fromChain,
        string memory toChain
    ) external payable nonReentrant whenNotPaused {
        require(msg.value > 0, "Bridge fee required");
        require(bytes(fromToken).length > 0, "Invalid from token");
        require(bytes(toToken).length > 0, "Invalid to token");
        require(amount > 0, "Invalid amount");
        
        uint256 swapId = _swapIdCounter++;
        
        CrossChainSwap memory newSwap = CrossChainSwap({
            swapId: swapId,
            user: msg.sender,
            fromToken: fromToken,
            toToken: toToken,
            amount: amount,
            price: price,
            fromChain: fromChain,
            toChain: toChain,
            timestamp: block.timestamp,
            executed: false,
            confirmed: false
        });
        
        crossChainSwaps[swapId] = newSwap;
        
        // Send cross-chain message via LayerZero
        _sendCrossChainMessage(swapId, fromChain, toChain);
        
        emit CrossChainSwapInitiated(swapId, msg.sender, fromToken, toToken, fromChain, toChain);
    }
    
    /**
     * @dev Confirm a cross-chain swap (called by LayerZero)
     * @param swapId The swap ID to confirm
     */
    function confirmCrossChainSwap(uint256 swapId) 
        internal 
        onlyValidSwap(swapId) 
    {
        CrossChainSwap storage swap = crossChainSwaps[swapId];
        require(!swap.confirmed, "Swap already confirmed");
        
        swap.confirmed = true;
    }
    
    /**
     * @dev Process message payload and extract swap data
     * @param messageId Message ID
     * @param payload Message payload
     */
    function _processMessagePayload(uint256 messageId, bytes memory payload) internal {
        // Decode payload to extract swap information
        // This is a simplified version - in production you'd use proper encoding
        
        BridgeMessage storage message = bridgeMessages[messageId];
        message.processed = true;
        
        // Extract swap ID from payload and confirm the swap
        // For demo purposes, we'll assume the payload contains the swap ID
        uint256 swapId = _extractSwapIdFromPayload(payload);
        if (swapId > 0) {
            confirmCrossChainSwap(swapId);
        }
    }
    
    /**
     * @dev Execute a cross-chain swap on destination chain
     * @param swapId The swap ID to execute
     */
    function executeCrossChainSwap(uint256 swapId) 
        external 
        onlyValidSwap(swapId) 
        whenNotPaused 
    {
        CrossChainSwap storage swap = crossChainSwaps[swapId];
        require(!swap.executed, "Swap already executed");
        require(swap.confirmed, "Swap not confirmed");
        
        swap.executed = true;
        
        // Here you would integrate with 1inch Fusion+ API
        // to execute the actual swap on the destination chain
        
        emit CrossChainSwapExecuted(swapId, swap.fromChain, swap.toChain, swap.amount);
    }
    
    /**
     * @dev Process incoming bridge message from LayerZero
     * @param sourceChainId Source chain ID
     * @param payload Message payload
     */
    function _processIncomingMessage(uint16 sourceChainId, bytes memory payload) 
        internal 
        onlySupportedChain(sourceChainId) 
    {
        uint256 messageId = _messageIdCounter++;
        
        BridgeMessage memory message = BridgeMessage({
            messageId: messageId,
            payload: payload,
            sourceChainId: sourceChainId,
            destinationChainId: 0, // Will be set based on payload
            timestamp: block.timestamp,
            processed: false
        });
        
        bridgeMessages[messageId] = message;
        
        // Process the message payload
        _processMessagePayload(messageId, payload);
        
        emit BridgeMessageReceived(messageId, sourceChainId, 0);
    }
    

    
    /**
     * @dev Send cross-chain message via LayerZero
     * @param swapId Swap ID
     * @param fromChain Source chain
     * @param toChain Destination chain
     */
    function _sendCrossChainMessage(
        uint256 swapId,
        string memory fromChain,
        string memory toChain
    ) internal {
        uint16 destinationChainId = chainNameToId[toChain];
        require(destinationChainId != 0, "Invalid destination chain");
        
        // Prepare payload
        bytes memory payload = abi.encode(swapId, fromChain, toChain);
        
        // Send via LayerZero
        lzEndpoint.send{value: msg.value}(
            destinationChainId,
            abi.encodePacked(address(this)), // destination address
            payload,
            payable(msg.sender), // refund address
            address(0), // zro payment address
            "" // adapter params
        );
    }
    
    /**
     * @dev Extract swap ID from payload (simplified)
     * @param payload Message payload
     */
    function _extractSwapIdFromPayload(bytes memory payload) internal pure returns (uint256) {
        // Simplified extraction - in production you'd use proper decoding
        if (payload.length >= 32) {
            uint256 swapId;
            assembly {
                swapId := mload(add(payload, 32))
            }
            return swapId;
        }
        return 0;
    }
    
    /**
     * @dev Add supported chain
     * @param chainId Chain ID
     * @param chainName Chain name
     */
    function addSupportedChain(uint16 chainId, string memory chainName) external onlyOwner {
        _addSupportedChain(chainId, chainName);
    }
    
    /**
     * @dev Remove supported chain
     * @param chainId Chain ID to remove
     */
    function removeSupportedChain(uint16 chainId) external onlyOwner {
        require(supportedChains[chainId], "Chain not supported");
        supportedChains[chainId] = false;
        emit ChainRemoved(chainId);
    }
    
    /**
     * @dev Internal function to add supported chain
     */
    function _addSupportedChain(uint16 chainId, string memory chainName) internal {
        supportedChains[chainId] = true;
        chainNameToId[chainName] = chainId;
        emit ChainSupported(chainId, chainName);
    }
    
    /**
     * @dev Get cross-chain swap data
     * @param swapId Swap ID
     */
    function getCrossChainSwap(uint256 swapId) 
        external 
        view 
        onlyValidSwap(swapId) 
        returns (CrossChainSwap memory) 
    {
        return crossChainSwaps[swapId];
    }
    
    /**
     * @dev Get bridge message data
     * @param messageId Message ID
     */
    function getBridgeMessage(uint256 messageId) external view returns (BridgeMessage memory) {
        return bridgeMessages[messageId];
    }
    
    /**
     * @dev Check if chain is supported
     * @param chainId Chain ID
     */
    function isChainSupported(uint16 chainId) external view returns (bool) {
        return supportedChains[chainId];
    }
    
    /**
     * @dev Get chain ID by name
     * @param chainName Chain name
     */
    function getChainIdByName(string memory chainName) external view returns (uint16) {
        return chainNameToId[chainName];
    }
    
    /**
     * @dev Pause bridge (emergency only)
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @dev Unpause bridge
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    /**
     * @dev Update LayerZero endpoint
     * @param newEndpoint New endpoint address
     */
    function updateLayerZeroEndpoint(address newEndpoint) external onlyOwner {
        require(newEndpoint != address(0), "Invalid endpoint");
        lzEndpoint = ILayerZeroEndpoint(newEndpoint);
    }
    
    /**
     * @dev Withdraw bridge fees
     */
    function withdrawFees() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }
    
    // LayerZero callback function
    function _nonblockingLzReceive(
        uint16 _srcChainId,
        bytes memory _srcAddress,
        uint64 _nonce,
        bytes memory _payload
    ) internal {
        _processIncomingMessage(_srcChainId, _payload);
    }
}

contracts/RewardSystem.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
/**
 * @title RewardSystem
 * @dev Gamified reward system for SwapScrolls
 * Handles points, achievements, and reputation tracking
 */
contract RewardSystem is Ownable, Pausable, ReentrancyGuard {
    // Structs
    struct Achievement {
        uint256 id;
        string name;
        string description;
        uint256 pointsReward;
        uint256 reputationReward;
        bool isActive;
        uint256 requiredSwaps;
        uint256 requiredVolume;
        uint256 requiredChains;
    }
    
    struct UserRewards {
        uint256 totalPoints;
        uint256 totalReputation;
        uint256 level;
        uint256 achievementsUnlocked;
        uint256 lastRewardTime;
        mapping(uint256 => bool) unlockedAchievements;
        mapping(string => uint256) activityStats;
    }
    
    struct RewardTier {
        uint256 tierId;
        string name;
        uint256 minPoints;
        uint256 minReputation;
        uint256 bonusMultiplier;
        bool isActive;
    }
    
    // State variables
    mapping(address => UserRewards) public userRewards;
    mapping(uint256 => Achievement) public achievements;
    mapping(uint256 => RewardTier) public rewardTiers;
    
    uint256 private _achievementIdCounter;
    uint256 private _tierIdCounter;
    
    // Reward tokens
    IERC20 public rewardToken;
    
    // Constants
    uint256 public constant BASE_POINTS_PER_SWAP = 10;
    uint256 public constant BASE_REPUTATION_PER_SWAP = 1;
    uint256 public constant CROSS_CHAIN_BONUS = 25;
    uint256 public constant VOLUME_BONUS_THRESHOLD = 1000 ether;
    uint256 public constant LEVEL_UP_THRESHOLD = 100;
    
    // Events
    event AchievementUnlocked(address indexed user, uint256 achievementId, string achievementName);
    event PointsEarned(address indexed user, uint256 points, string reason);
    event ReputationEarned(address indexed user, uint256 reputation, string reason);
    event LevelUp(address indexed user, uint256 newLevel);
    event TierUpgraded(address indexed user, uint256 newTier);
    event RewardClaimed(address indexed user, uint256 amount);
    
    // Modifiers
    modifier onlyValidAchievement(uint256 achievementId) {
        require(achievements[achievementId].id != 0, "Invalid achievement");
        _;
    }
    
    modifier onlyValidTier(uint256 tierId) {
        require(rewardTiers[tierId].tierId != 0, "Invalid tier");
        _;
    }
    
    constructor(address _rewardToken) Ownable(msg.sender) {
        rewardToken = IERC20(_rewardToken);
        _achievementIdCounter = 1;
        _tierIdCounter = 1;
        _initializeAchievements();
        _initializeRewardTiers();
    }
    
    /**
     * @dev Award points and reputation for a swap
     * @param user User address
     * @param swapAmount Swap amount
     * @param isCrossChain Whether it's a cross-chain swap
     * @param chainCount Number of chains involved
     */
    function awardSwapRewards(
        address user,
        uint256 swapAmount,
        bool isCrossChain,
        uint256 chainCount
    ) external whenNotPaused {
        UserRewards storage rewards = userRewards[user];
        
        // Base points for swap
        uint256 basePoints = BASE_POINTS_PER_SWAP;
        uint256 baseReputation = BASE_REPUTATION_PER_SWAP;
        
        // Volume bonus
        if (swapAmount >= VOLUME_BONUS_THRESHOLD) {
            basePoints *= 2;
            baseReputation *= 2;
        }
        
        // Cross-chain bonus
        if (isCrossChain) {
            basePoints += CROSS_CHAIN_BONUS;
            baseReputation += 5;
        }
        
        // Multi-chain bonus
        if (chainCount > 1) {
            basePoints += (chainCount - 1) * 10;
            baseReputation += (chainCount - 1) * 2;
        }
        
        // Update user stats
        rewards.totalPoints += basePoints;
        rewards.totalReputation += baseReputation;
        rewards.activityStats["swaps"]++;
        rewards.activityStats["volume"] += swapAmount;
        rewards.lastRewardTime = block.timestamp;
        
        // Check for level up
        _checkLevelUp(user);
        
        // Check for achievements
        _checkAchievements(user);
        
        emit PointsEarned(user, basePoints, "swap_executed");
        emit ReputationEarned(user, baseReputation, "swap_executed");
    }
    
    /**
     * @dev Award bonus points for special activities
     * @param user User address
     * @param points Points to award
     * @param reason Reason for the bonus
     */
    function awardBonusPoints(
        address user,
        uint256 points,
        string memory reason
    ) external whenNotPaused {
        UserRewards storage rewards = userRewards[user];
        rewards.totalPoints += points;
        rewards.lastRewardTime = block.timestamp;
        
        _checkLevelUp(user);
        
        emit PointsEarned(user, points, reason);
    }
    
    /**
     * @dev Award reputation for activities
     * @param user User address
     * @param reputation Reputation to award
     * @param reason Reason for the reputation
     */
    function awardReputation(
        address user,
        uint256 reputation,
        string memory reason
    ) external whenNotPaused {
        UserRewards storage rewards = userRewards[user];
        rewards.totalReputation += reputation;
        rewards.lastRewardTime = block.timestamp;
        
        emit ReputationEarned(user, reputation, reason);
    }
    
    /**
     * @dev Claim rewards based on points and tier
     * @param amount Amount to claim
     */
    function claimRewards(uint256 amount) external nonReentrant whenNotPaused {
        UserRewards storage rewards = userRewards[msg.sender];
        require(amount > 0, "Invalid amount");
        require(amount <= rewards.totalPoints, "Insufficient points");
        
        // Calculate reward amount based on tier
        uint256 rewardAmount = _calculateRewardAmount(msg.sender, amount);
        
        // Transfer reward tokens
        require(rewardToken.transfer(msg.sender, rewardAmount), "Reward transfer failed");
        
        // Deduct points
        rewards.totalPoints -= amount;
        
        emit RewardClaimed(msg.sender, rewardAmount);
    }
    
    /**
     * @dev Unlock an achievement for a user
     * @param user User address
     * @param achievementId Achievement ID
     */
    function unlockAchievement(address user, uint256 achievementId) 
        external 
        onlyValidAchievement(achievementId) 
        whenNotPaused 
    {
        UserRewards storage rewards = userRewards[user];
        Achievement storage achievement = achievements[achievementId];
        
        require(!rewards.unlockedAchievements[achievementId], "Achievement already unlocked");
        require(achievement.isActive, "Achievement not active");
        
        // Check if user meets requirements
        require(
            rewards.activityStats["swaps"] >= achievement.requiredSwaps &&
            rewards.activityStats["volume"] >= achievement.requiredVolume &&
            rewards.activityStats["chains"] >= achievement.requiredChains,
            "Requirements not met"
        );
        
        // Award points and reputation
        rewards.totalPoints += achievement.pointsReward;
        rewards.totalReputation += achievement.reputationReward;
        rewards.unlockedAchievements[achievementId] = true;
        rewards.achievementsUnlocked++;
        
        emit AchievementUnlocked(user, achievementId, achievement.name);
        emit PointsEarned(user, achievement.pointsReward, "achievement_unlocked");
        emit ReputationEarned(user, achievement.reputationReward, "achievement_unlocked");
    }
    
    /**
     * @dev Get user rewards data
     * @param user User address
     */
    function getUserRewards(address user) external view returns (
        uint256 totalPoints,
        uint256 totalReputation,
        uint256 level,
        uint256 achievementsUnlocked,
        uint256 lastRewardTime
    ) {
        UserRewards storage rewards = userRewards[user];
        return (
            rewards.totalPoints,
            rewards.totalReputation,
            rewards.level,
            rewards.achievementsUnlocked,
            rewards.lastRewardTime
        );
    }
    
    /**
     * @dev Get user activity stats
     * @param user User address
     * @param activity Activity name
     */
    function getUserActivityStat(address user, string memory activity) external view returns (uint256) {
        return userRewards[user].activityStats[activity];
    }
    
    /**
     * @dev Check if user has unlocked achievement
     * @param user User address
     * @param achievementId Achievement ID
     */
    function hasUnlockedAchievement(address user, uint256 achievementId) external view returns (bool) {
        return userRewards[user].unlockedAchievements[achievementId];
    }
    
    /**
     * @dev Get achievement data
     * @param achievementId Achievement ID
     */
    function getAchievement(uint256 achievementId) 
        external 
        view 
        onlyValidAchievement(achievementId) 
        returns (Achievement memory) 
    {
        return achievements[achievementId];
    }
    
    /**
     * @dev Get reward tier data
     * @param tierId Tier ID
     */
    function getRewardTier(uint256 tierId) 
        external 
        view 
        onlyValidTier(tierId) 
        returns (RewardTier memory) 
    {
        return rewardTiers[tierId];
    }
    
    /**
     * @dev Calculate reward amount based on user's tier
     * @param user User address
     * @param points Points to convert
     */
    function _calculateRewardAmount(address user, uint256 points) internal view returns (uint256) {
        UserRewards storage rewards = userRewards[user];
        uint256 userTier = _getUserTier(user);
        
        if (userTier > 0) {
            RewardTier storage tier = rewardTiers[userTier];
            return points * tier.bonusMultiplier / 100;
        }
        
        return points; // Base 1:1 conversion
    }
    
    /**
     * @dev Get user's current tier
     * @param user User address
     */
    function _getUserTier(address user) internal view returns (uint256) {
        UserRewards storage rewards = userRewards[user];
        
        for (uint256 i = 1; i <= _tierIdCounter; i++) {
            RewardTier storage tier = rewardTiers[i];
            if (tier.isActive && 
                rewards.totalPoints >= tier.minPoints && 
                rewards.totalReputation >= tier.minReputation) {
                return i;
            }
        }
        
        return 0; // No tier
    }
    
    /**
     * @dev Check if user should level up
     * @param user User address
     */
    function _checkLevelUp(address user) internal {
        UserRewards storage rewards = userRewards[user];
        uint256 newLevel = rewards.totalPoints / LEVEL_UP_THRESHOLD + 1;
        
        if (newLevel > rewards.level) {
            rewards.level = newLevel;
            emit LevelUp(user, newLevel);
        }
    }
    
    /**
     * @dev Check for achievements
     * @param user User address
     */
    function _checkAchievements(address user) internal {
        UserRewards storage rewards = userRewards[user];
        
        for (uint256 i = 1; i <= _achievementIdCounter; i++) {
            Achievement storage achievement = achievements[i];
            
            if (achievement.isActive && 
                !rewards.unlockedAchievements[i] &&
                rewards.activityStats["swaps"] >= achievement.requiredSwaps &&
                rewards.activityStats["volume"] >= achievement.requiredVolume &&
                rewards.activityStats["chains"] >= achievement.requiredChains) {
                
                // Auto-unlock achievement
                rewards.unlockedAchievements[i] = true;
                rewards.achievementsUnlocked++;
                rewards.totalPoints += achievement.pointsReward;
                rewards.totalReputation += achievement.reputationReward;
                
                emit AchievementUnlocked(user, i, achievement.name);
                emit PointsEarned(user, achievement.pointsReward, "achievement_unlocked");
                emit ReputationEarned(user, achievement.reputationReward, "achievement_unlocked");
            }
        }
    }
    
    /**
     * @dev Initialize default achievements
     */
    function _initializeAchievements() internal {
        _addAchievement("First Swap", "Complete your first swap", 50, 10, 1, 0, 1);
        _addAchievement("Volume Trader", "Trade over 1000 ETH in volume", 200, 25, 0, 1000 ether, 1);
        _addAchievement("Cross-Chain Explorer", "Swap across 3 different chains", 300, 50, 0, 0, 3);
        _addAchievement("Swap Master", "Complete 100 swaps", 500, 100, 100, 0, 1);
        _addAchievement("Legendary Trader", "Trade over 10000 ETH in volume", 1000, 200, 0, 10000 ether, 1);
    }
    
    /**
     * @dev Initialize reward tiers
     */
    function _initializeRewardTiers() internal {
        _addRewardTier("Bronze", 0, 0, 100, true);
        _addRewardTier("Silver", 1000, 50, 125, true);
        _addRewardTier("Gold", 5000, 100, 150, true);
        _addRewardTier("Platinum", 10000, 200, 200, true);
        _addRewardTier("Diamond", 25000, 500, 300, true);
    }
    
    /**
     * @dev Add new achievement
     */
    function _addAchievement(
        string memory name,
        string memory description,
        uint256 pointsReward,
        uint256 reputationReward,
        uint256 requiredSwaps,
        uint256 requiredVolume,
        uint256 requiredChains
    ) internal {
        uint256 achievementId = _achievementIdCounter;
        _achievementIdCounter++;
        
        achievements[achievementId] = Achievement({
            id: achievementId,
            name: name,
            description: description,
            pointsReward: pointsReward,
            reputationReward: reputationReward,
            isActive: true,
            requiredSwaps: requiredSwaps,
            requiredVolume: requiredVolume,
            requiredChains: requiredChains
        });
    }
    
    /**
     * @dev Add new reward tier
     */
    function _addRewardTier(
        string memory name,
        uint256 minPoints,
        uint256 minReputation,
        uint256 bonusMultiplier,
        bool isActive
    ) internal {
        uint256 tierId = _tierIdCounter;
        _tierIdCounter++;
        
        rewardTiers[tierId] = RewardTier({
            tierId: tierId,
            name: name,
            minPoints: minPoints,
            minReputation: minReputation,
            bonusMultiplier: bonusMultiplier,
            isActive: isActive
        });
    }
    
    /**
     * @dev Add new achievement (admin only)
     */
    function addAchievement(
        string memory name,
        string memory description,
        uint256 pointsReward,
        uint256 reputationReward,
        uint256 requiredSwaps,
        uint256 requiredVolume,
        uint256 requiredChains
    ) external onlyOwner {
        _addAchievement(name, description, pointsReward, reputationReward, requiredSwaps, requiredVolume, requiredChains);
    }
    
    /**
     * @dev Add new reward tier (admin only)
     */
    function addRewardTier(
        string memory name,
        uint256 minPoints,
        uint256 minReputation,
        uint256 bonusMultiplier,
        bool isActive
    ) external onlyOwner {
        _addRewardTier(name, minPoints, minReputation, bonusMultiplier, isActive);
    }
    
    /**
     * @dev Pause reward system (emergency only)
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @dev Unpause reward system
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    /**
     * @dev Update reward token
     * @param newRewardToken New reward token address
     */
    function updateRewardToken(address newRewardToken) external onlyOwner {
        require(newRewardToken != address(0), "Invalid token address");
        rewardToken = IERC20(newRewardToken);
    }
}

contracts/SuiBridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

/**
 * @title SuiBridge
 * @dev Bridge contract for Sui blockchain integration
 * Handles cross-chain NFT transfers and swap data synchronization
 */
contract SuiBridge is Ownable, Pausable, ReentrancyGuard {
    using Strings for uint256;
    
    // Structs
    struct SuiNFT {
        string suiObjectId;
        uint256 ethereumTokenId;
        address ethereumOwner;
        string metadata;
        uint256 bridgeTimestamp;
        bool isBridged;
    }
    
    struct BridgeRequest {
        uint256 requestId;
        address user;
        uint256 ethereumTokenId;
        string suiDestination;
        uint256 timestamp;
        bool processed;
        bool confirmed;
    }
    
    struct SuiSwapData {
        string suiObjectId;
        string fromToken;
        string toToken;
        uint256 amount;
        uint256 price;
        uint256 timestamp;
        bool success;
    }
    
    // State variables
    mapping(string => SuiNFT) public suiNFTs;
    mapping(uint256 => BridgeRequest) public bridgeRequests;
    mapping(string => SuiSwapData[]) public suiSwapHistory;
    mapping(address => uint256[]) public userBridgeRequests;
    
    uint256 private _requestIdCounter;
    
    // Sui network configuration
    string public constant SUI_NETWORK = "mainnet";
    string public constant SUI_PACKAGE_ID = "0x..."; // Sui package ID
    string public constant SUI_MODULE = "swap_scrolls";
    
    // Events
    event BridgeRequestCreated(
        uint256 indexed requestId,
        address indexed user,
        uint256 ethereumTokenId,
        string suiDestination
    );
    
    event NFTBridgedToSui(
        uint256 indexed requestId,
        string suiObjectId,
        uint256 ethereumTokenId
    );
    
    event SuiSwapExecuted(
        string indexed suiObjectId,
        string fromToken,
        string toToken,
        uint256 amount
    );
    
    event BridgeRequestConfirmed(uint256 indexed requestId);
    event BridgeRequestProcessed(uint256 indexed requestId);
    
    // Modifiers
    modifier onlyValidRequest(uint256 requestId) {
        require(bridgeRequests[requestId].requestId != 0, "Invalid request ID");
        _;
    }
    
    modifier onlyRequestOwner(uint256 requestId) {
        require(bridgeRequests[requestId].user == msg.sender, "Not request owner");
        _;
    }
    
    constructor() Ownable(msg.sender) {
        _requestIdCounter = 1;
    }
    
    /**
     * @dev Create a bridge request to transfer NFT to Sui
     * @param ethereumTokenId Ethereum NFT token ID
     * @param suiDestination Sui destination address
     * @param metadata NFT metadata for Sui
     */
    function createBridgeRequest(
        uint256 ethereumTokenId,
        string memory suiDestination,
        string memory metadata
    ) external nonReentrant whenNotPaused {
        require(bytes(suiDestination).length > 0, "Invalid Sui destination");
        require(bytes(metadata).length > 0, "Invalid metadata");
        
        uint256 requestId = _requestIdCounter++;
        
        BridgeRequest memory newRequest = BridgeRequest({
            requestId: requestId,
            user: msg.sender,
            ethereumTokenId: ethereumTokenId,
            suiDestination: suiDestination,
            timestamp: block.timestamp,
            processed: false,
            confirmed: false
        });
        
        bridgeRequests[requestId] = newRequest;
        userBridgeRequests[msg.sender].push(requestId);
        
        emit BridgeRequestCreated(requestId, msg.sender, ethereumTokenId, suiDestination);
    }
    
    /**
     * @dev Process bridge request (called by Sui validator)
     * @param requestId Bridge request ID
     * @param suiObjectId Sui object ID for the bridged NFT
     */
    function processBridgeRequest(uint256 requestId, string memory suiObjectId) 
        external 
        onlyValidRequest(requestId) 
        whenNotPaused 
    {
        BridgeRequest storage request = bridgeRequests[requestId];
        require(!request.processed, "Request already processed");
        
        request.processed = true;
        
        // Create SuiNFT record
        SuiNFT memory suiNFT = SuiNFT({
            suiObjectId: suiObjectId,
            ethereumTokenId: request.ethereumTokenId,
            ethereumOwner: request.user,
            metadata: "",
            bridgeTimestamp: block.timestamp,
            isBridged: true
        });
        
        suiNFTs[suiObjectId] = suiNFT;
        
        emit BridgeRequestProcessed(requestId);
        emit NFTBridgedToSui(requestId, suiObjectId, request.ethereumTokenId);
    }
    
    /**
     * @dev Confirm bridge request (called by LayerZero or validator)
     * @param requestId Bridge request ID
     */
    function confirmBridgeRequest(uint256 requestId) 
        external 
        onlyValidRequest(requestId) 
    {
        BridgeRequest storage request = bridgeRequests[requestId];
        require(!request.confirmed, "Request already confirmed");
        
        request.confirmed = true;
        
        emit BridgeRequestConfirmed(requestId);
    }
    
    /**
     * @dev Record Sui swap execution
     * @param suiObjectId Sui object ID
     * @param fromToken Token being swapped from
     * @param toToken Token being swapped to
     * @param amount Amount being swapped
     * @param price Price of the swap
     */
    function recordSuiSwap(
        string memory suiObjectId,
        string memory fromToken,
        string memory toToken,
        uint256 amount,
        uint256 price
    ) external whenNotPaused {
        require(bytes(suiObjectId).length > 0, "Invalid Sui object ID");
        require(suiNFTs[suiObjectId].isBridged, "NFT not bridged to Sui");
        
        SuiSwapData memory swapData = SuiSwapData({
            suiObjectId: suiObjectId,
            fromToken: fromToken,
            toToken: toToken,
            amount: amount,
            price: price,
            timestamp: block.timestamp,
            success: true
        });
        
        suiSwapHistory[suiObjectId].push(swapData);
        
        emit SuiSwapExecuted(suiObjectId, fromToken, toToken, amount);
    }
    
    /**
     * @dev Get bridge request data
     * @param requestId Request ID
     */
    function getBridgeRequest(uint256 requestId) 
        external 
        view 
        onlyValidRequest(requestId) 
        returns (BridgeRequest memory) 
    {
        return bridgeRequests[requestId];
    }
    
    /**
     * @dev Get Sui NFT data
     * @param suiObjectId Sui object ID
     */
    function getSuiNFT(string memory suiObjectId) external view returns (SuiNFT memory) {
        return suiNFTs[suiObjectId];
    }
    
    /**
     * @dev Get Sui swap history
     * @param suiObjectId Sui object ID
     */
    function getSuiSwapHistory(string memory suiObjectId) external view returns (SuiSwapData[] memory) {
        return suiSwapHistory[suiObjectId];
    }
    
    /**
     * @dev Get user's bridge requests
     * @param user User address
     */
    function getUserBridgeRequests(address user) external view returns (uint256[] memory) {
        return userBridgeRequests[user];
    }
    
    /**
     * @dev Check if NFT is bridged to Sui
     * @param ethereumTokenId Ethereum token ID
     */
    function isNFTBridgedToSui(uint256 ethereumTokenId) external view returns (bool) {
        // This would require iterating through all SuiNFTs
        // In production, you'd maintain a reverse mapping
        return false; // Simplified for demo
    }
    
    /**
     * @dev Get Sui object ID for Ethereum token
     * @param ethereumTokenId Ethereum token ID
     */
    function getSuiObjectId(uint256 ethereumTokenId) external view returns (string memory) {
        // This would require a reverse mapping
        // In production, you'd maintain ethereumTokenId => suiObjectId mapping
        return ""; // Simplified for demo
    }
    
    /**
     * @dev Generate Sui transaction payload for NFT minting
     * @param requestId Bridge request ID
     */
    function generateSuiMintPayload(uint256 requestId) 
        external 
        view 
        onlyValidRequest(requestId) 
        returns (bytes memory) 
    {
        BridgeRequest storage request = bridgeRequests[requestId];
        
        // Generate Sui transaction payload
        // This would contain the move call data for minting the NFT on Sui
        bytes memory payload = abi.encode(
            SUI_PACKAGE_ID,
            SUI_MODULE,
            "mint_swap_scroll",
            request.ethereumTokenId,
            request.suiDestination,
            request.user
        );
        
        return payload;
    }
    
    /**
     * @dev Generate Sui transaction payload for swap execution
     * @param suiObjectId Sui object ID
     * @param fromToken Token to swap from
     * @param toToken Token to swap to
     * @param amount Amount to swap
     */
    function generateSuiSwapPayload(
        string memory suiObjectId,
        string memory fromToken,
        string memory toToken,
        uint256 amount
    ) external view returns (bytes memory) {
        require(suiNFTs[suiObjectId].isBridged, "NFT not bridged to Sui");
        
        // Generate Sui transaction payload for swap
        bytes memory payload = abi.encode(
            SUI_PACKAGE_ID,
            SUI_MODULE,
            "execute_swap",
            suiObjectId,
            fromToken,
            toToken,
            amount
        );
        
        return payload;
    }
    
    /**
     * @dev Update Sui network configuration
     * @param newPackageId New package ID
     * @param newModule New module name
     */
    function updateSuiConfig(string memory newPackageId, string memory newModule) external onlyOwner {
        // In a real implementation, these would be state variables
        // For demo purposes, we'll just emit an event
        emit SuiConfigUpdated(newPackageId, newModule);
    }
    
    /**
     * @dev Pause bridge (emergency only)
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @dev Unpause bridge
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    // Additional events
    event SuiConfigUpdated(string packageId, string module);
    
    // Sui-specific helper functions
    function getSuiNetworkInfo() external pure returns (string memory network, string memory packageId) {
        return (SUI_NETWORK, SUI_PACKAGE_ID);
    }
    
    function validateSuiAddress(string memory suiAddress) external pure returns (bool) {
        // Basic validation for Sui address format
        // In production, you'd implement proper Sui address validation
        return bytes(suiAddress).length == 66 && 
               bytes(suiAddress)[0] == "0" && 
               bytes(suiAddress)[1] == "x";
    }
}

contracts/SwapScrollNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title SwapScrollNFT
 * @dev Main NFT contract for SwapScrolls - cross-chain, gamified DeFi NFTs
 * Built on top of 1inch Fusion+ with LayerZero and Sui integration
 */
contract SwapScrollNFT is ERC721, ERC721URIStorage, Ownable, Pausable, ReentrancyGuard {
    uint256 private _tokenIds;
    
    // Structs
    struct SwapScroll {
        uint256 tokenId;
        address owner;
        string scrollType; // "basic", "advanced", "legendary"
        uint256 swapCount;
        uint256 totalVolume;
        uint256 reputation;
        uint256 createdAt;
        uint256 lastSwapAt;
        bool isTradable;
        string crossChainData;
        uint256 rewardPoints;
    }
    
    struct SwapData {
        string fromToken;
        string toToken;
        uint256 amount;
        uint256 price;
        uint256 timestamp;
        string chainId;
        bool success;
    }
    
    // State variables
    mapping(uint256 => SwapScroll) public swapScrolls;
    mapping(uint256 => SwapData[]) public swapHistory;
    mapping(address => uint256[]) public userScrolls;
    mapping(string => uint256) public scrollTypeCount;
    
    // Events
    event SwapScrollMinted(uint256 indexed tokenId, address indexed owner, string scrollType);
    event SwapExecuted(uint256 indexed tokenId, string fromToken, string toToken, uint256 amount);
    event CrossChainSwap(uint256 indexed tokenId, string fromChain, string toChain);
    event RewardEarned(uint256 indexed tokenId, uint256 points, string reason);
    event ReputationUpdated(uint256 indexed tokenId, uint256 newReputation);
    event ScrollTraded(uint256 indexed tokenId, address from, address to, uint256 price);
    
    // Constants
    uint256 public constant MINT_PRICE = 0.01 ether;
    uint256 public constant TRADE_FEE = 25; // 0.25%
    uint256 public constant MAX_SUPPLY = 10000;
    
    // Modifiers
    modifier onlyScrollOwner(uint256 tokenId) {
        require(ownerOf(tokenId) == msg.sender, "Not scroll owner");
        _;
    }
    
    modifier scrollExists(uint256 tokenId) {
        require(ownerOf(tokenId) != address(0), "Scroll does not exist");
        _;
    }
    
    constructor() ERC721("SwapScrolls", "SWAPSCROLL") Ownable(msg.sender) {
        _tokenIds = 1; // Start from 1
    }
    
    /**
     * @dev Mint a new SwapScroll NFT
     * @param scrollType Type of scroll to mint
     * @param metadataURI Metadata URI for the NFT
     */
    function mintSwapScroll(string memory scrollType, string memory metadataURI) 
        external 
        payable 
        nonReentrant 
        whenNotPaused 
    {
        require(msg.value >= MINT_PRICE, "Insufficient mint price");
        require(_tokenIds <= MAX_SUPPLY, "Max supply reached");
        require(bytes(scrollType).length > 0, "Invalid scroll type");
        
        uint256 newTokenId = _tokenIds;
        _tokenIds++;
        
        // Create new SwapScroll
        SwapScroll memory newScroll = SwapScroll({
            tokenId: newTokenId,
            owner: msg.sender,
            scrollType: scrollType,
            swapCount: 0,
            totalVolume: 0,
            reputation: 100, // Base reputation
            createdAt: block.timestamp,
            lastSwapAt: 0,
            isTradable: true,
            crossChainData: "",
            rewardPoints: 0
        });
        
        swapScrolls[newTokenId] = newScroll;
        userScrolls[msg.sender].push(newTokenId);
        scrollTypeCount[scrollType]++;
        
        _safeMint(msg.sender, newTokenId);
        _setTokenURI(newTokenId, metadataURI);
        
        emit SwapScrollMinted(newTokenId, msg.sender, scrollType);
    }
    
    /**
     * @dev Execute a swap and update scroll data
     * @param tokenId The scroll NFT ID
     * @param fromToken Token being swapped from
     * @param toToken Token being swapped to
     * @param amount Amount being swapped
     * @param price Price of the swap
     * @param chainId Chain where swap occurred
     */
    function executeSwap(
        uint256 tokenId,
        string memory fromToken,
        string memory toToken,
        uint256 amount,
        uint256 price,
        string memory chainId
    ) external onlyScrollOwner(tokenId) scrollExists(tokenId) {
        SwapScroll storage scroll = swapScrolls[tokenId];
        
        // Update scroll data
        scroll.swapCount++;
        scroll.totalVolume += amount;
        scroll.lastSwapAt = block.timestamp;
        
        // Create swap record
        SwapData memory swapRecord = SwapData({
            fromToken: fromToken,
            toToken: toToken,
            amount: amount,
            price: price,
            timestamp: block.timestamp,
            chainId: chainId,
            success: true
        });
        
        swapHistory[tokenId].push(swapRecord);
        
        // Calculate rewards and reputation
        uint256 rewardPoints = calculateRewards(amount, scroll.scrollType);
        scroll.rewardPoints += rewardPoints;
        scroll.reputation += calculateReputationGain(amount, scroll.scrollType);
        
        emit SwapExecuted(tokenId, fromToken, toToken, amount);
        emit RewardEarned(tokenId, rewardPoints, "swap_executed");
        emit ReputationUpdated(tokenId, scroll.reputation);
    }
    
    /**
     * @dev Execute cross-chain swap
     * @param tokenId The scroll NFT ID
     * @param fromChain Source chain
     * @param toChain Destination chain
     * @param bridgeData Bridge transaction data
     */
    function executeCrossChainSwap(
        uint256 tokenId,
        string memory fromChain,
        string memory toChain,
        string memory bridgeData
    ) external onlyScrollOwner(tokenId) scrollExists(tokenId) {
        SwapScroll storage scroll = swapScrolls[tokenId];
        
        // Update cross-chain data
        scroll.crossChainData = bridgeData;
        scroll.swapCount++;
        scroll.lastSwapAt = block.timestamp;
        
        // Bonus rewards for cross-chain swaps
        uint256 crossChainBonus = 50;
        scroll.rewardPoints += crossChainBonus;
        scroll.reputation += 25;
        
        emit CrossChainSwap(tokenId, fromChain, toChain);
        emit RewardEarned(tokenId, crossChainBonus, "cross_chain_bonus");
        emit ReputationUpdated(tokenId, scroll.reputation);
    }
    
    /**
     * @dev Trade a SwapScroll NFT
     * @param tokenId The scroll NFT ID
     * @param newOwner New owner address
     */
    function tradeScroll(uint256 tokenId, address newOwner) 
        external 
        onlyScrollOwner(tokenId) 
        scrollExists(tokenId) 
    {
        SwapScroll storage scroll = swapScrolls[tokenId];
        require(scroll.isTradable, "Scroll not tradable");
        
        address previousOwner = ownerOf(tokenId);
        
        // Transfer ownership
        _transfer(previousOwner, newOwner, tokenId);
        
        // Update scroll data
        scroll.owner = newOwner;
        
        // Update user scrolls arrays
        removeFromUserScrolls(previousOwner, tokenId);
        userScrolls[newOwner].push(tokenId);
        
        emit ScrollTraded(tokenId, previousOwner, newOwner, 0);
    }
    
    /**
     * @dev Get scroll data
     * @param tokenId The scroll NFT ID
     */
    function getScrollData(uint256 tokenId) 
        external 
        view 
        scrollExists(tokenId) 
        returns (SwapScroll memory) 
    {
        return swapScrolls[tokenId];
    }
    
    /**
     * @dev Get swap history for a scroll
     * @param tokenId The scroll NFT ID
     */
    function getSwapHistory(uint256 tokenId) 
        external 
        view 
        scrollExists(tokenId) 
        returns (SwapData[] memory) 
    {
        return swapHistory[tokenId];
    }
    
    /**
     * @dev Get user's scrolls
     * @param user User address
     */
    function getUserScrolls(address user) external view returns (uint256[] memory) {
        return userScrolls[user];
    }
    
    /**
     * @dev Calculate rewards based on swap amount and scroll type
     */
    function calculateRewards(uint256 amount, string memory scrollType) internal pure returns (uint256) {
        uint256 baseReward = amount / 1000; // 0.1% of swap amount
        
        if (keccak256(abi.encodePacked(scrollType)) == keccak256(abi.encodePacked("legendary"))) {
            return baseReward * 3;
        } else if (keccak256(abi.encodePacked(scrollType)) == keccak256(abi.encodePacked("advanced"))) {
            return baseReward * 2;
        } else {
            return baseReward;
        }
    }
    
    /**
     * @dev Calculate reputation gain
     */
    function calculateReputationGain(uint256 amount, string memory scrollType) internal pure returns (uint256) {
        uint256 baseGain = 1;
        
        if (keccak256(abi.encodePacked(scrollType)) == keccak256(abi.encodePacked("legendary"))) {
            return baseGain * 3;
        } else if (keccak256(abi.encodePacked(scrollType)) == keccak256(abi.encodePacked("advanced"))) {
            return baseGain * 2;
        } else {
            return baseGain;
        }
    }
    
    /**
     * @dev Remove scroll from user's scrolls array
     */
    function removeFromUserScrolls(address user, uint256 tokenId) internal {
        uint256[] storage userScrollsArray = userScrolls[user];
        for (uint256 i = 0; i < userScrollsArray.length; i++) {
            if (userScrollsArray[i] == tokenId) {
                userScrollsArray[i] = userScrollsArray[userScrollsArray.length - 1];
                userScrollsArray.pop();
                break;
            }
        }
    }
    
    /**
     * @dev Pause contract (emergency only)
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @dev Unpause contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    /**
     * @dev Withdraw contract balance
     */
    function withdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }
    
    function tokenURI(uint256 tokenId) public view override(ERC721, ERC721URIStorage) returns (string memory) {
        return super.tokenURI(tokenId);
    }
    
    function supportsInterface(bytes4 interfaceId) public view override(ERC721, ERC721URIStorage) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

